import React, { useState, useEffect } from "react";
import "./App.css";
import ProductTableContainer from './container/ProductTableContainer'
import ProductSummaryContainer from './container/ProductSummaryContainer'
//https://mocki.io/v1/cd569385-602e-4776-99a1-edb3f911d869
import {fetchData} from '../src/actions/actions';
import {ApplicationState} from '../src/types'
import BannerLogContainer from './container/BannerLogContainer'
import { useDispatch, useSelector } from "react-redux";



function App(){
  const [isloading, setIsLoading]=useState(false)
  const data = useSelector((state: ApplicationState) => state.data);
  const error = useSelector((state: ApplicationState) => state.error);

  const dispatch = useDispatch();

  useEffect(() => {
    setIsLoading(true)
    dispatch(fetchData());
    setIsLoading(false)

  }, [dispatch])
  if (isloading) {
    return <div>Loading...</div>;
  }
  if (error) {
    return <div>Error: {error}</div>;
  }
  return (
    <div className="product-dashboard">
      <BannerLogContainer />
      {data && data.length !== 0 && (
        <div className="product-container">
          <ProductSummaryContainer  data={data} />
         <ProductTableContainer  data={data} />

         </div>
        )}


      </div>

  );
}



export default App;
