import React, { useState } from "react";
import {ApplicationState,createProduct,Product,WeeklySales} from '../utills/types'
import DataTable from 'react-data-table-component';
interface ProductTableProps {
    sales: any;
  }
const ProductTable: React.FC<ProductTableProps> = ({sales}) => {




  const columns = [
    {
      name: 'Week Ending',
      selector: (row: WeeklySales) => new Date(row.weekEnding).toLocaleDateString(),
      sortable: true,
      cell: (row:WeeklySales) => <span style={{ fontSize: '18px', fontWeight: 'bold' }}>{new Date(row.weekEnding).toLocaleDateString()}</span>,
    },
    {
      name: 'Retail Sales',
      selector: (row: WeeklySales) => "$" + row.retailSales.toLocaleString(),
      sortable: true,
      cell: (row:WeeklySales) => <span style={{ fontSize: '16px' }}>${row.retailSales.toLocaleString()}</span>,
    },
    {
      name: 'Wholesale Sales',
      selector: (row: WeeklySales) => "$" + row.wholesaleSales.toLocaleString(),
      sortable: true,
      cell: (row:WeeklySales) => <span style={{ fontSize: '16px' }}>${row.wholesaleSales.toLocaleString()}</span>,
    },
    {
      name: 'Units Sold',
      selector: (row: WeeklySales) => row.unitsSold.toLocaleString(),
      sortable: true,
      cell: (row:WeeklySales) => <span style={{ fontSize: '14px' }}>{row.unitsSold.toLocaleString()}</span>,
    },
    {
      name: 'Retailer Margin',
      selector: (row: WeeklySales) => "$" + row.retailerMargin.toLocaleString(),
      sortable: true,
      cell: (row:WeeklySales) => <span style={{ fontSize: '16px' }}>${row.retailerMargin.toLocaleString()}</span>,
    },
  ];

          const noDataComponent = (
            <div>No data to display</div>
          );

          return (
            <div className="sales-column-table">
              <DataTable
              className="sales-column-table"
                columns={columns}
                data={sales}
                defaultSortFieldId={1}
                noDataComponent={noDataComponent}
              />
            </div>
          );
        };



  export default ProductTable;
