import React, { useState } from "react";
import {createProduct} from '../utills/types'
import {formatMonth} from '../utills/graph'
import { LineChart, Line, XAxis, Tooltip } from "recharts";
interface ProductGraphContainer {
    data: any[]; // Replace `any` with the appropriate type if you have it
  }


const ProductGraphContainer: React.FC<ProductGraphContainer>= ({data}) => {

    const product = createProduct(data[0]);
    const sales = product.sales || [];
    const transformedData = sales.map(item => ({
        ...item,
        month: formatMonth(item.weekEnding),
    }));
  return (
    <div id="chart">
      <LineChart width={900} height={400} data={transformedData}>
        <XAxis dataKey="month" />

        <Line type="monotone" dataKey="retailSales" stroke="#40a8ef" />
        <Line type="monotone" dataKey="wholesaleSales" stroke="#3c4858" />
      </LineChart>
    </div>
  );
};

  export default ProductGraphContainer;
