import {ApplicationState,ActionTypes,createProduct,WeeklySales,createSaleData} from '../types'
import {  useSelector } from "react-redux";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCaretDown } from "@fortawesome/free-solid-svg-icons";
import ProductTable from '../components/ProductTable';

interface ProductTableContainer {
  data: any[]; // Replace `any` with the appropriate type if you have it
}
const ProductTableContainer: React.FC<ProductTableContainer> = ({data}) => {
  const product = createProduct(data[0]);
  const sales = product.sales || [];

  console.log('sales',sales);



  const rows = sales.length > 0 ? (
    sales.map((row: WeeklySales, index: number) => (
      <tr key={index}>
        <td>{new Date(row.weekEnding).toLocaleDateString()}</td>
        <td>{"$" + row.retailSales.toLocaleString()}</td>
        <td>{"$" + row.wholesaleSales.toLocaleString()}</td>
        <td>{row.unitsSold.toLocaleString()}</td>
        <td>{"$" + row.retailerMargin.toLocaleString()}</td>
      </tr>
    ))
  ): (
    <tr>
      <td colSpan={5}>No data to display</td>
    </tr>
  );
  const arrowIcon = <FontAwesomeIcon icon={faCaretDown} />;
console.log('rows',rows);
    return (

      <div className="sales-column-table">
        <ProductTable sales={sales}/>
        </div>

    );
  };





  export default ProductTableContainer;
